const { getProducts, getOffers } = require("../models/querys");

// get all products
// const getProducts = async(req, res) => {
//     try {
//         const connection = await getConnection();
//         const result = await connection.query("SELECT * FROM product");
//         console.log(result);
//         res.json(result);

//     } catch (error) {
//         res.status(500);
//         res.send(error);
//     }

// }

// get products in offer
// gets only first 8 products ordered by DESC
exports.getAllOffers = async () => {
  // call method from models querys.js
  const products = await getProducts();
  const offers = products
    .filter((product) => {
      return product.discount > 0;
    })
    .sort((a, b) => {
      return b.discount - a.discount;
    })
    .slice(0, 8);
  return offers;
};

